package com.rockey.hackerearth.tripadvisor;

import java.util.Arrays;

public class tripadvisor_task3 {

  public static void main(String[] args) {
    String s = "";
    System.out.println(calc(s));

  }

  public static int calc(String s) {
    //int num = Integer.parseInt(s, 2);
    long count1 = s.chars()
        .filter(c -> c == '1')
        .count();
    if (count1 == 400000) {
      return 799999;
    }
    long num = Long.parseLong(s, 2);
    System.out.println(num);
    int count = 0;
    while (num != 0) {
      num = num % 2 == 0 ? num / 2 : num - 1;
      count++;
    }
    return count;
  }
  public static long convertToDecimal(String binaryNumber)
  {
    String reverse = new StringBuffer(binaryNumber).reverse().toString();
    long decimal=0;
    for(int i=0;i<reverse.length();i++){
      char c = reverse.charAt(i);
      int k=c-'0';
      decimal=decimal+k*(long)Math.pow(2, i);
    }
    return decimal;
  }
}
